CREATE TABLE "core"."comments" ("id" serial NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "author_id" integer NOT NULL, "context" text NOT NULL, "section_version_id" integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("section_version_id") REFERENCES "core"."section_versions"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("author_id") REFERENCES "core"."users"("id") ON UPDATE restrict ON DELETE restrict);
